mapper.update:
update MerchDevice set
curStoreId=#{curStoreId},
curShopId=#{curShopId},
mender=#{mender},
mendTime=#{mendTime}
where merchId=#{merchId} and deviceId=#{deviceId};
update Device set
curStoreId=#{curStoreId},
curShopId=#{curShopId},
mender=#{mender},
mendTime=#{mendTime}
where curMerchId=#{merchId} and id=#{deviceId}
